import React, { Component } from 'react';
import './MyComponent.css';

export default class MainApp extends Component {
    // We can export default or by naming the component (mostly done when there is more than one component in one file) 
    constructor(props) {
        super(props);

        // constructor is the first method that is called for a react component
        // so we use this to set any information we need , prior to the rest of the code
        // such as intialize state , and binding helper functions to this , so we can use "this" in our helper functions.

        this.state = {
            count: 0
        };

        this.incrementCount = this.incrementCount.bind(this);
        this.getButton = this.getButton.bind(this);
    }

    //In this space we can define any helper functions
    incrementCount() {
        this.setState({
            count: this.state.count + 1
        });
    }

    getButton() {
        return (
            <div onClick={this.incrementCount}>Count Plus One</div>
        );
    }

	render() {
        // render is required, and is basically used to setup any variables 
        //that you might want to return
        var countDiv = (
        <div>Count: {this.state.count}</div>
        );

        var countButton = this.getButton();

        // return basically includes all jsx elements ... for example divs , 
        // span , and you can also add variables or components like such { variable } or <Component />
        // there needs to be one main div or jsx element that encloses everything that is being returned 
		return (
			<div className='MyComponent'>
                <div className='Title'>This is MyComponent !</div>
                {countDiv}
                {countButton}
			</div>
		);
	}
}
