import React, { Component } from 'react';
import './Dialog.css';

export default class Dialog extends Component {
    render() {
        classes = {
            Dialog: true,
            hidden: this.props.showDialog
        }
        return (
            <div classes>
                <div className='DialogTitle'>My Dialog</div>
                <div className='Contents'>
                    Contents of the Dialog: 
                        - one
                        - two
                        - three 
                </div>
            </div>
        );
    }
}
