import React, { Component } from "react";
import Child from "../Tab/Tab";
import "./Tabs.css";

export default class Parent extends Component {
  clickHandler(e) {
    console.log("Parent component clicked.. ", e.target);
  }
  render() {
    var allTabs = [];
    var i;
    for (i = 1; i < 3; i++) {
      allTabs.push(<Child key={i} index={i} />);
    }

    return (
      <div onClick={this.clickHandler} className="parent">
        {allTabs}
      </div>
    );
  }
}
