import React, { Component } from 'react';
import './Dialog.css';

export default class Dialog extends Component {
    constructor(props) {
        super(props);

        this.state = {
            diffFromMouseToDialogX: 0,
            diffFromMouseToDialogY: 0,
            dragging: false,
            styles: {}
        }
    }

    _dragStart(e) {
        this.setState({
            diffFromMouseToDialogX: e.screenX - e.currentTarget.getBoundingClientRect().left,
            diffFromMouseToDialogY: e.screenY - e.currentTarget.getBoundingClientRect().top,
            dragging: true
        });
    }

    _dragging(e) {
        if(this.state.dragging) {
            var top = e.screenY - this.state.diffFromMouseToDialogY;
            var left = e.screenX - this.state.diffFromMouseToDialogX;

            this.setState({
                styles: {
                    top: top,
                    left: left
                }
            });
        }
    }

    _dragEnd() {
        this.setState({
            dragging: false
        });
    }


    render() {
        var classes = this.props.show ? 'Dialog' : 'Dialog hidden';
        return (
            <div className={classes} style={this.state.styles} onMouseDown={this._dragStart} onMouseMove={this._dragging} onMouseUp={this._dragEnd}>
                <div className='DialogTitle'>My Dialog</div>
                <div className='Contents'>
                    Contents of the Dialog: 
                        - one
                        - two
                        - three 
                </div>
                <div className='closeButton' onClick={this.props.onClose}>
                    Close
                </div>
            </div>
        );
    }
}
